import React, {useState} from 'react'
import {
    HeroContainer,
    HeroBg,
    HeroContent,
    HeroH1,
    ArrowForward,
    ArrowRight,
    FormWrap,
    FormContent,
    Form,
    FormLabel,
    IdeaInput,
    EmailInput,
    FormButton,
} from './HeroElements';
// import Video from '../../videos/video.mp4';
import './index.css';
import Img from "../../images/background.jpg";
import useForm from '../../useForm';

const HeroSection = () => {
    const [hover, setHover] = useState(false);

    const onHover = () => {
        setHover(!hover);
    }

    const {handleChange, values} = useForm(); 

    return (
        <HeroContainer>
            <HeroBg style={{
                    backgroundImage: `url(${Img})`,
                    height: "100%", width: "100%", backgroundRepeat: "no-repeat", backgroundSize: 'cover'
                }} >
                {/* <VideoBg autoPlay={true} loop muted src={Video} type="video/mp4" /> */}
            </HeroBg>
            <HeroContent>
                <HeroH1>Type your business idea, thoughts, feelings, confessions or what you had for breakfast this morning into the big yellow box. If we like your idea, we'll go with it and give you back 10% of the profits. That's it!</HeroH1>
                <br />
                <FormWrap>
                    <FormContent>
                        <Form>
                            <FormLabel htmlFor='idea'>Your idea</FormLabel>
                            <IdeaInput 
                            id='idea' 
                            type='text' 
                            name='idea' 
                            required 
                            value={values.idea}
                            onChange={handleChange}
                            />
                            <FormLabel htmlFor='email'>Email</FormLabel>
                            <EmailInput 
                            id='email' 
                            type='email' 
                            name='email' 
                            required 
                            value={values.email}
                            onChange={handleChange}
                            />
                            <FormButton type='submit' onMouseEnter={onHover} 
                                onMouseLeave={onHover} 
                                primary='true' 
                                dark='true'>
                                    Submit {hover ? <ArrowForward /> : <ArrowRight />}
                            </FormButton>
                        </Form>
                    </FormContent>
                </FormWrap>
            </HeroContent>
        </HeroContainer>
    )
}

export default HeroSection;
