import React, {useState} from 'react'
import {
    HeroContainer,
    HeroBg,
    VideoBg,
    HeroContent,
    HeroH1,
    ArrowForward,
    ArrowRight,
    FormWrap,
    FormContent,
    Form,
    FormH1,
    FormLabel,
    FormInput,
    FormButton,
} from './HeroElements';
import Video from '../../videos/video.mp4';
import './index.css';

const HeroSection = () => {
    const [hover, setHover] = useState(false);

    const onHover = () => {
        setHover(!hover);
    }

    return (
        <HeroContainer>
            <HeroBg>
                <VideoBg autoPlay loop muted src={Video} type="video/mp4" />
            </HeroBg>
            <HeroContent>
                <HeroH1>Make your ideas a reality with us...</HeroH1>
                <br />
                <FormWrap>
                    <FormContent>
                        <Form action="#">
                            <FormH1>Enter your idea here...</FormH1>
                            <FormLabel htmlFor='for'>Your idea</FormLabel>
                            <FormInput type='text' required />
                            <FormLabel htmlFor='for'>Email</FormLabel>
                            <FormInput type='email' required />
                            <FormButton type='submit' onMouseEnter={onHover} 
                                onMouseLeave={onHover} 
                                primary='true' 
                                dark='true'>
                                    Submit {hover ? <ArrowForward /> : <ArrowRight />}
                            </FormButton>
                        </Form>
                    </FormContent>
                </FormWrap>
            </HeroContent>
        </HeroContainer>
    )
}

export default HeroSection;
