import React, {useState} from 'react'
import {
    HeroContainer,
    HeroBg,
    VideoBg,
    HeroContent,
    HeroH1,
    ArrowForward,
    ArrowRight,
    FormWrap,
    FormContent,
    Form,
    FormLabel,
    IdeaInput,
    EmailInput,
    FormButton,
} from './HeroElements';
import Video from '../../videos/video.mp4';
import './index.css';

const HeroSection = () => {
    const [hover, setHover] = useState(false);

    const onHover = () => {
        setHover(!hover);
    }

    return (
        <HeroContainer>
            <HeroBg>
                <div style={{
                    backgroundImage: 'url("https://media.geeksforgeeks.org/'+
                    'wp-content/uploads/20201221222410/download3.png")',
                    height: "300px", backgroundRepeat: "no-repeat", backgroundSize: 'cover'
                }} />
                {/* <VideoBg autoPlay={true} loop muted src={Video} type="video/mp4" /> */}
            </HeroBg>
            <HeroContent>
                <HeroH1>Type your business idea, thoughts, feelings, confessions or what you had for breakfast this morning into the big yellow box. If we like your idea, we'll go with it and give you back 10% of the profits. That's it!</HeroH1>
                <br />
                <FormWrap>
                    <FormContent>
                        <Form action="#">
                            {/* <FormH1>Enter your idea here...</FormH1> */}
                            <FormLabel htmlFor='for'>Your idea</FormLabel>
                            <IdeaInput type='text' required />
                            <FormLabel htmlFor='for'>Enter your email address if you'd like to be contacted</FormLabel>
                            <EmailInput type='email' required />
                            <FormButton type='submit' onMouseEnter={onHover} 
                                onMouseLeave={onHover} 
                                primary='true' 
                                dark='true'>
                                    Submit {hover ? <ArrowForward /> : <ArrowRight />}
                            </FormButton>
                        </Form>
                    </FormContent>
                </FormWrap>
            </HeroContent>
        </HeroContainer>
    )
}

export default HeroSection;
