import React, {useState} from 'react'
import {
    HeroContainer,
    HeroBg,
    VideoBg,
    HeroContent,
    HeroH1,
    ArrowForward,
    ArrowRight,
    FormWrap,
    FormContent,
    Form,
    FormLabel,
    IdeaInput,
    EmailInput,
    FormButton,
} from './HeroElements';
import Video from '../../videos/video.mp4';
import './index.css';
import Img from "../../images/background.jpg";

const HeroSection = () => {
    const [hover, setHover] = useState(false);

    const onHover = () => {
        setHover(!hover);
    }

    return (
        <HeroContainer>
            <HeroBg style={{
                    backgroundImage: `url(${Img})`,
                    height: "100%", width: "100%", backgroundRepeat: "no-repeat", backgroundSize: 'cover'
                }} >
                {/* <VideoBg autoPlay={true} loop muted src={Video} type="video/mp4" /> */}
            </HeroBg>
            <HeroContent>
                <HeroH1>Type your business idea, thoughts, feelings, confessions or what you had for breakfast this morning into the big yellow box. If we like your idea, we'll go with it and give you back 10% of the profits. That's it!</HeroH1>
                <br />
                <FormWrap>
                    <FormContent>
                        <form className="form">
                            <div className="form-inputs">
                                <FormLabel htmlFor='idea'>Your idea</FormLabel>
                                <textarea 
                                type="text"
                                name="idea"
                                className="form-idea"
                                placeholder="Enter your idea..."/>



                            </div>
                            {/* <FormH1>Enter your idea here...</FormH1> */}

                            {/* <IdeaInput type='text' required />
                            <FormLabel htmlFor='for'>Enter your email address if you'd like to be contacted</FormLabel>
                            <EmailInput type='email' required /> */}
                            <FormButton type='submit' onMouseEnter={onHover} 
                                onMouseLeave={onHover} 
                                primary='true' 
                                dark='true'>
                                    Submit {hover ? <ArrowForward /> : <ArrowRight />}
                            </FormButton>
                        </form>
                    </FormContent>
                </FormWrap>
            </HeroContent>
        </HeroContainer>
    )
}

export default HeroSection;
